import {MovieInterface} from "../Interface/ResponsesInterfaces";
import { Link } from "react-router-dom";
import ReviewForm from './ReviewForm.tsx'
import getReview from "./Hooks/getMovies.tsx";

export default function MoviesSingle({}) {

    const getReviewList = getReview();
    const [reviewList, seReviewList] = useState<MovieInterface[]>([]);
    const [needsUpdate, setNeedsUpdate] = useState<boolean>(false);

    useEffect(() => {
      getReviewList().then(data => {
          setReviewList(data)
          setNeedsUpdate(false)
        })

    }, [needsUpdate])


    return (
        <>
            <div className="row justify-content-between">
                <div className='col-sm bg-light rounded'>
                    <img src={require(`../assets/images/films/avatar.jpeg`)} width="300px"/>
                    <h3>Test</h3>
                    <p>
                        <small>
                            Par : Test
                            <br/>
                            Sortie le : Test
                        </small>
                    </p>
                </div>

                <ReviewForm />
            </div>

            <div className="row justify-content-between">
                <ReviewList />
            </div>
        </>

    )
}
