import logo from './logo.svg';
import './App.css';

import {useEffect, useState} from "react";
import useLogin from "./Hook/useLogin";
import {MovieInterface} from "./Interface/ResponsesInterfaces";
import {LocalUserInterface} from "./Interface/LocalUserInterface";
import LoginForm from "./Component/LoginForm";
import HideIfLogged from "./Component/HideIfLogged";
import useRegister from "./Hooks/useRegister";
import getMovies from "./Hooks/getMovies.tsx";


import BlogForm from "./Component/BlogForm.tsx";
import useGetCookies from "./Hooks/useGetCookies";
import useEraseCookie from "./Hooks/useEraseCookie";

export default function App() {

  const getMovieList = getMovies();
  const [movieList, setMovieList] = useState<BlogInterface[]>([])
  const [needsUpdate, setNeedsUpdate] = useState<boolean>(false)

  useEffect(() => {
        getMovieList()
            .then(data => {
                setMovieList(data)
                setNeedsUpdate(false)
            })
    }, [needsUpdate])


  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p>
          Edit <code>src/App.js</code> and save to reload.
        </p>
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>
      </header>
    </div>
  );
}
