import { Link } from "react-router-dom";
import logo from './assets/images/logo_imdb.svg';
import { FaMoon } from 'react-icons/fa';
import {FiLogOut} from 'react-icons/fi';
import useEraseCookie from "./Hooks/eraseCookies.tsx";

export default function Nav({setTheme, theme, setLoggedUser, loggedUser}){

  const eraseCookie = useEraseCookie();

  const themeToggle = () => {
    theme === 'light' ? setTheme("dark") : setTheme("light");
  }

  const handleDisconnect = () => {
        setLoggedUser({
            status: 'error',
            token: "",
            username: ""
        });
        eraseCookie();
  }

  console.log('user', loggedUser);

  return(
    <nav className={`navbar navbar-expand-lg navbar-light bg-light ${theme}`}>
      <img src={logo} alt="" width="80px"/>

      <div className="collapse navbar-collapse" id="navbarTogglerDemo02">
        <ul className="navbar-nav mr-auto mt-2 mt-lg-0">
          <li className="nav-item active">
            <Link to="/" className="nav-link">Films</Link>
          </li>

        </ul>

        <ul className="navbar-nav">
          <li className="nav-item switch_input">
            <label className="switch">
              <input type="checkbox" onChange={() => themeToggle()} />
              <span className="slider round"></span>
            </label>
          </li>
          <li className="nav-item">
            <Link to="/login" className="nav-link">Login</Link>
          </li>
          <li className="nav-item">
            <Link to="/login" className="nav-link"><FiLogOut /></Link>
          </li>
        </ul>
      </div>
    </nav>
  )
}
